<?php session_start(); require 'db.php'; if (!isset($_SESSION['user_id'])) { die("Login required"); } $user_id = $_SESSION['user_id']; $stmt = $conn->prepare("SELECT mood, note, created_at FROM moods WHERE user_id = ? ORDER BY created_at DESC"); $stmt->bind_param("i", $user_id); $stmt->execute(); $result = $stmt->get_result(); $moods = []; while ($row = $result->fetch_assoc()) { $moods[] = $row; } header('Content-Type: application/json'); echo json_encode($moods); ?>
